mapper.sql:
select contract.* ,project.project_name,project.project_no,dd_sa.dic_data_name as storage_addr_name, client.client_name as project_client_name , otherstaff.staff_name as sales_username from tb_contract contract INNER JOIN vb_project project on contract.project_id = project.project_id left join tb_client client on project.project_client_id = client.client_id left join tb_other_staff otherstaff on contract.sales_userid = otherstaff.staff_id left join ts_dic_data dd_sa on contract.storage_addr = dd_sa.id WHERE 1=1
AND project.project_id = #{Contract.project_id}
AND project.project_name like concat('%',TRIM(#{Contract.project_name}),'%')
AND contract.contract_no like concat('%',TRIM(#{Contract.contract_no}),'%')
and contract.submit_date >= #{Contract.date1}
and contract.submit_date <= #{Contract.date2}
order by contract.build_datetime desc
