mapper.insert:
 	 insert into tb_depart_costs
	 (
	 	id, staff_id, dept_id,project_id, pay_item_id,use_month,pay_date,import_order,
	  	amount,description,build_datetime,build_username,build_userid,delete_flag
	)values  (
		#{id}, #{staff_id},  #{dept_id}, #{project_id}, #{pay_item_id}, #{use_month}, #{pay_date}, #{import_order}, 
	  	#{amount},#{description}, #{build_datetime}, #{build_username}, #{build_userid}, #{delete_flag}
	 )

mapper.sql:
select rc.* ,project.project_name,project.project_no, staff.staff_no, dept.dept_name , dc.dic_data_name as pay_item_name, if(staff.staff_id = null or staff.staff_id = '' , rc.staff_id , staff.staff_name) as staff_name , aa.need_approve from tb_depart_costs rc left JOIN vb_project project on rc.project_id = project.project_id left JOIN tb_other_staff staff on rc.staff_id = staff.staff_id join ts_dept dept on rc.dept_id = dept.dept_id left join ts_dic_data dc on rc.pay_item_id = dc.id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'DEPART_COSTS' group by data_id) aa on rc.id = aa.data_id WHERE rc.delete_flag = '0'
AND project.project_id = #{DepartCosts.project_id}
AND project.project_name like concat('%',TRIM(#{DepartCosts.project_name}),'%')
AND project.project_no like concat('%',TRIM(#{DepartCosts.project_no}),'%')
AND rc.pay_item_id = #{DepartCosts.pay_item_id}
AND dept.dept_id = #{DepartCosts.dept_id}
AND ( staff.staff_name like concat('%',TRIM(#{DepartCosts.staff_name}),'%') or rc.staff_id like concat('%',TRIM(#{DepartCosts.staff_name}),'%') )
AND rc.use_month >= #{DepartCosts.use_month1}
AND rc.use_month <= #{DepartCosts.use_month2}
AND rc.pay_date >= #{DepartCosts.pay_date1}
AND rc.pay_date <= #{DepartCosts.pay_date2}
and rc.verify_userid is not null
and rc.verify_userid is null
order by rc.use_month desc,project.build_datetime desc, rc.build_datetime, rc.import_order
