mapper.update:
update  personnel_monthly_bonus set 
join_datetime=#{join_datetime},
change_time=#{change_time},
amount=#{amount},
description=#{description}
where id=#{id}

mapper.sql:
select pmb.* ,dept.dept_name,dept.dept_no ,sc.staff_no,sc.staff_name,aa.need_approve, pmbs.join_datetime,pmbs.change_time,pmbs.amount,pmbs.description from personnel_monthly_base pmb inner join personnel_monthly_bonus pmbs on pmb.id = pmbs.id inner join tb_other_staff sc on pmb.staff_id = sc.staff_id left JOIN ts_dept dept on pmb.dept_id = dept.dept_id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'PERSONNELMONTHLYBASE' group by data_id) aa on pmb.id = aa.data_id WHERE 1=1
AND pmb.the_month = #{PersonnelMonthlyBonus.the_month}
AND pmb.staff_id = #{PersonnelMonthlyBonus.staff_id}
AND pmb.monthly_type = #{PersonnelMonthlyBonus.monthly_type}
and pmb.verify_userid is not null
and pmb.verify_userid is null
order by pmb.monthly_type, pmb.the_month,pmb.build_datetime , pmb.import_order
