mapper.sql:
select psc.* ,dept.dept_name, staff.staff_name , staff.staff_no , salary.work_days as total_work_days from tb_other_staff_cost psc INNER JOIN ts_dept dept on psc.dept_id = dept.dept_id inner join tb_other_staff staff on psc.staff_id = staff.staff_id inner join tb_other_salary salary on psc.salary_id = salary.salary_id WHERE 1 = 1
AND psc.dept_id = #{OtherStaffCost.dept_id}
AND staff.staff_id = #{OtherStaffCost.staff_id}
AND staff.staff_name like concat('%',TRIM(#{OtherStaffCost.staff_name}),'%')
AND staff.staff_no like concat('%',TRIM(#{OtherStaffCost.staff_no}),'%')
AND psc.work_month = #{OtherStaffCost.work_month}
AND psc.work_month >= #{OtherStaffCost.work_month1}
AND psc.work_month <= #{OtherStaffCost.work_month2}
order by psc.work_month desc,dept.build_time , staff.build_datetime , staff.import_order
