mapper.sql:
select pmb.* ,project.project_name,project.project_no ,sc.staff_no,sc.staff_name,aa.need_approve, pms.join_datetime,pms.change_time,pms.old_salary,pms.new_salary,pms.curr_salary,pms.description from personnel_monthly_base pmb inner join personnel_monthly_salary pms on pmb.id = pms.id inner join tb_staff_costs sc on pmb.staff_id = sc.staff_id left JOIN tb_project project on pmb.project_id = project.project_id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'PERSONNELMONTHLYBASE' group by data_id) aa on pmb.id = aa.data_id WHERE 1=1
AND pmb.the_month = #{PersonnelMonthlySalary.the_month}
AND pmb.staff_id = #{PersonnelMonthlySalary.staff_id}
AND pmb.monthly_type = #{PersonnelMonthlySalary.monthly_type}
and pmb.verify_userid is not null
and pmb.verify_userid is null
order by pmb.monthly_type, pmb.the_month,pmb.build_datetime , pmb.import_order
