mapper.sql:
select psc.* ,project.project_name,project.project_no, staff.staff_name , staff.staff_no , salary.work_days as total_work_days from tb_project_staff_cost psc INNER JOIN vb_project project on psc.project_id = project.project_id inner join tb_staff_costs staff on psc.staff_id = staff.staff_id inner join tb_salary salary on psc.salary_id = salary.salary_id WHERE 1 = 1
AND project.deptid = #{ProjectStaffCost.dept_id}
AND project.project_id = #{ProjectStaffCost.project_id}
AND project.project_name like concat('%',TRIM(#{ProjectStaffCost.project_name}),'%')
AND project.project_no like concat('%',TRIM(#{ProjectStaffCost.project_no}),'%')
AND staff.staff_id = #{ProjectStaffCost.staff_id}
AND staff.staff_name like concat('%',TRIM(#{ProjectStaffCost.staff_name}),'%')
AND psc.work_month = #{ProjectStaffCost.work_month}
AND psc.work_month >= #{ProjectStaffCost.work_month1}
AND psc.work_month <= #{ProjectStaffCost.work_month2}
order by psc.work_month desc,project.build_datetime desc, staff.build_datetime desc, staff.import_order
