mapper.insert:
 	 insert into tb_reimburse_costs
	 (
	 	reimburse_id, staff_id, project_id, pay_item_id,use_month,pay_date,import_order,
	  	amount,description,build_datetime,build_username,build_userid,delete_flag
	)values  (
		#{reimburse_id}, #{staff_id}, #{project_id}, #{pay_item_id}, #{use_month}, #{pay_date}, #{import_order}, 
	  	#{amount},#{description}, #{build_datetime}, #{build_username}, #{build_userid}, #{delete_flag}
	 )

mapper.sql:
select rc.* ,project.project_name,project.project_no, staff.staff_no, dept.dept_name , dept.dept_id , dd_rai.dic_data_name as pay_item_name , case when staff.staff_name is NULL then rc.staff_id else staff.staff_name end as staff_name, aa.need_approve from tb_reimburse_costs rc INNER JOIN vb_project project on rc.project_id = project.project_id left JOIN tb_staff_costs staff on rc.staff_id = staff.staff_id left join ts_dept dept on project.deptid = dept.dept_id left join ts_dic_data dd_rai on rc.pay_item_id = dd_rai.id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'REIMBURSE_COSTS' group by data_id) aa on rc.reimburse_id = aa.data_id WHERE rc.delete_flag = '0'
AND project.project_id = #{ReimburseCosts.project_id}
AND project.project_name like concat('%',TRIM(#{ReimburseCosts.project_name}),'%')
AND project.project_no like concat('%',TRIM(#{ReimburseCosts.project_no}),'%')
AND rc.pay_item_id = #{ReimburseCosts.pay_item_id}
AND dept.dept_id = #{ReimburseCosts.dept_id}
AND ( staff.staff_name like concat('%',TRIM(#{ReimburseCosts.staff_name}),'%') or rc.staff_id like concat('%',TRIM(#{ReimburseCosts.staff_name}),'%') )
AND rc.use_month >= #{ReimburseCosts.use_month1}
AND rc.use_month <= #{ReimburseCosts.use_month2}
AND rc.pay_date >= #{ReimburseCosts.pay_date1}
AND rc.pay_date <= #{ReimburseCosts.pay_date2}
and rc.verify_userid is not null
and rc.verify_userid is null
order by rc.use_month desc, project.build_datetime desc, rc.build_datetime desc, rc.import_order
