mapper.select:
select sp.* ,dept.dept_name , staff.staff_no , staff.staff_name from tb_staff_performance sp left JOIN ts_dept dept on sp.dept_id = dept.dept_id join tb_other_staff staff on sp.staff_id = staff.staff_id where 1=1
AND sp.the_month = #{the_month}
AND sp.staff_id = #{staff_id}
AND sp.dept_id = #{dept_id}
and sp.verify_userid is not null
and sp.verify_userid is null

select count(*) c
from tb_staff_performance sp
where sp.the_month = #{the_month}
and sp.verify_userid is null

and sp.staff_id in (
	select staff_id from tb_other_work_attendance
	where attendance_month = #{the_month}			
	and dept_id = #{dept_id}
)

mapper.delete:
delete from  tb_staff_performance
where id=#{id}
and verify_userid is null

mapper.sql:
select sp.* ,dept.dept_name , staff.staff_no , staff.staff_name , aa.need_approve from tb_staff_performance sp left JOIN ts_dept dept on sp.dept_id = dept.dept_id join tb_other_staff staff on sp.staff_id = staff.staff_id left join (SELECT need_approve,data_id FROM tb_apply_approve where delete_flag='0' and need_approve='1' and data_type = 'STAFF_PERFORMANCE' group by data_id) aa on sp.id = aa.data_id WHERE 1=1
AND sp.the_month = #{StaffPerformance.the_month}
AND sp.staff_id = #{StaffPerformance.staff_id}
AND sp.dept_id = #{StaffPerformance.dept_id}
AND staff.staff_no like concat('%',TRIM(#{StaffPerformance.staff_no}),'%')
AND staff.staff_name like concat('%',TRIM(#{StaffPerformance.staff_name}),'%')
AND sp.the_month >= #{StaffPerformance.the_month1}
AND sp.the_month <= #{StaffPerformance.the_month2}
and sp.verify_userid is not null
and sp.verify_userid is null
and (sp.verify_userid is not null and aa.need_approve='1')
order by sp.the_month desc , dept.dept_no
